import "./ExpenseItem.css";

function ExpenseItem(props) { //all attributes can recive here with one parameter (coming from app.js) ///////////içeri sadece props ya da {date,title,amount} bu şekilde kullan
  return (
    <div className="expense-item">
      <div>{prop.date.toISOString()}</div>
      <div className="expense-item__description">
        <h2>{prop.title}</h2>
        <div className="expense-item__price">${prop.amount}</div>
      </div>
    </div>
  );
}

export default ExpenseItem;
